package LogicQuestion;

public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=10;
		int add=0;
		int sub=0;
		for(int i=1;i>=0;i++)
		{
		add=a+b;
		sub=a-b;
		}
		System.out.println("the add is ="+add+"the sub is "+sub);
	}

}
